  CREATE TABLE IF NOT EXISTS `drop_data` (
    `id` bigint(20) NOT NULL AUTO_INCREMENT,
    `dropperid` int(11) NOT NULL,
    `itemid` int(11) NOT NULL DEFAULT '0',
    `minimum_quantity` int(11) NOT NULL DEFAULT '1',
